import React, {  useState } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import { addToFavorite, getMovies } from "../../actions";
import './Buscador.css';



export function Buscador (props) {

  const [title, setTitle] = useState("")


  function handleChange(event) {
    setTitle(event.target.value );
  }
  function handleSubmit(event) {
    event.preventDefault();
    props.getMovies(title)
  }


    return (
      <div>
        <h2>Buscador</h2>
        <form className="form-container" onSubmit={(e) => handleSubmit(e)}>
          <div>
            <label className="label" htmlFor="title">Pel√≠cula: </label>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => handleChange(e)}
            />
          </div>
          <button type="submit">BUSCAR</button>
        </form>
        <ul>
         {/* Aqui tienes que escribir tu codigo para mostrar la lista de peliculas */}
         {
           props.moviesListed.length === 0 ? <h2>Busca alguna pelicula</h2> : props.moviesListed.map(movies => 
            <li key={movies.imdbID}>
            <Link to={`movie/${movies.imdbID}`}>  <h6 style={{display:'inline-block', margin: '1% auto'}}>{movies.Title}</h6> </Link>
            <button onClick={() => props.addToFavorite({title: props.Title, id: props.imdbID})}>fav</button>
            </li>
            )
         }
        </ul>
      </div>
    );

}

function mapStateToProps(state) {
  return {
    moviesListed: state.moviesListed
  }
}

function mapDispatchToProps(dispatch) {
  return {
    getMovies: title => dispatch(getMovies(title)),
    addToFavorite: (id) => dispatch(addToFavorite(id)),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Buscador);
