const initialState = {
    moviesListed: [],
}

export default function rootReducer (state=initialState, action) {
    switch (action.type) {
        case 'GET_MOVIES':
            return {
                ...state,
                moviesListed: action.movies.Search
            };
        
        
        default:
            return state;
    }
}
