import React from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import { removeFromFavorites } from "../../actions";
import './Favorites.css';

export function ConnectedList (props) {


    return (
      <div>
        <h2>Películas Favoritas</h2>
        <ul>
          {
            props.favorites.length === 0 ? 
            <h2>No hay ninguna pelicula en tu lista</h2>
            :
            props.favorites.map(movies => 
              <li key={movies.imdbID}>
                <img src={movies.Poster} alt={'not found'} />
              <Link to={`/movie/${movies.imdbID}`}> {movies.Title} </Link>
              {props.favorites.indexOf(movies)=== -1?<button className='' onClick={() => props.addToFavorites(movies)}>☆</button>:<button className='' onClick={() => props.removeFromFavorites(movies.imdbID)}>⭐</button>}
              </li>
              )
          }
        </ul>
      </div>
    );
}

function mapStateToProps (state) {
  return {
    favorites: state.favorites
  }
}

function mapDispatchToProps (dispatch) {
  return {
    removeFromFavorites: id => dispatch(removeFromFavorites(id))
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(ConnectedList);
