import React from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import { removeFromFavorites } from "../../actions";
import './Favorites.css';

export function ConnectedList (props) {


    return (
      <div>
        <h2>PelÃ­culas Favoritas</h2>
        <ul>
          {/* Aqui deberias poner tu lista de peliculas! */}
          {
            props.favorites.length === 0 ? 
            <h2>No hay ninguna pelicula en tu lista</h2>
            :
            props.favorites.map(movies => 
              <li key={movies.id}>
              <Link to={`/movie/${movies.id}`}> {movies.title} </Link>
              <button onClick={() => props.removeFromFavorites(movies.id)}>ðŸ—‘</button>
              </li>
              )
          }
        </ul>
      </div>
    );
}

function mapStateToProps (state) {
  return {
    favorites: state.favorites
  }
}

function mapDispatchToProps (dispatch) {
  return {
    removeFromFavorites: id => dispatch(removeFromFavorites(id))
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(ConnectedList);
