import React from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import { removeFromFavorites } from "../../actions";
import './Favorites.css';

export function ConnectedList (props) {


    return (
      <div>
        <h2>Pel√≠culas Favoritas</h2>
        <ul>
          {
            props.favorites.length === 0 ? 
            <h2>No hay ninguna pelicula en tu lista</h2>
            :
            props.favorites.map(movies => 
              <div  key={movies.imdbID}>
            <div class="container">
              <div class="cellphone-container">    
                  <div class="movie">       
                    <img src={movies.Poster} class="movie-img" alt="img not found"/>
                    <div class="text-movie-cont">
                      <div class="mr-grid">
                        <div class="col1">
                          <Link to={`/movie/${movies.imdbID}`}> <h1>{movies.Title}</h1> </Link>
                          <ul class="movie-gen">
                            <li>PG-13  /</li>
                            <li>1h 49min  /</li>
                            <li>Adventure, Drama, Sci-Fi</li>
                          </ul>
                        </div>
                      </div>
                      <div class="mr-grid summary-row">
                        <div class="col2">
                          <h5>RESUMEN</h5>
                        </div>
                      </div>
                      <div class="mr-grid">
                        <div class="col1">
                          <p class="movie-description">                Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. </p>
                        </div>
                      </div>
                      <div class="mr-grid action-row">
                        <div class="col2"><div class="watch-btn"><h3>VER DETALLES</h3></div>

                        <div class="col2"><div class="watch-btn"><button onClick={(imdbID) => props.removeFromFavorites(imdbID)}>ELIMINAR</button></div></div>
                        </div>
                      </div>
                    </div>
                  </div>
              </div>
            </div>
            </div>
              )

          }
        </ul>
      </div>
    );
}

function mapStateToProps (state) {
  return {
    moviesListed: state.moviesListed,
    favorites: state.favorites
  }
}

function mapDispatchToProps (dispatch) {
  return {
    removeFromFavorites: id => dispatch(removeFromFavorites(id))
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(ConnectedList);
