import React, {  useState } from "react";
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import { addToFavorite, getMovies } from "../../actions";
import './Buscador.css';



export function Buscador (props) {

  const [title, setTitle] = useState("")


  function handleChange(event) {
    setTitle(event.target.value );
  }
  function handleSubmit(event) {
    event.preventDefault();
    props.getMovies(title)
  }


    return (
      <div>
        <h2>Buscador</h2>
        <form className="form-container" onSubmit={(e) => handleSubmit(e)}>
          <div>
            <input
              type="text"
              id="title"
              autoComplete="off"
              value={title}
              onChange={(e) => handleChange(e)}
              placeholder='Busca una pelicula...'
            />
          </div>
          <button type="submit">BUSCAR</button>
        </form>
        <ul id='moviesList'>
         {
           props.moviesListed.length === 0 ? <h2>Busca alguna pelicula</h2> : props.moviesListed.map(movies => 
            <div className='movieCard' key={movies.imdbID}>
              <Link to={`movie/${movies.imdbID}`}>  <h4 style={{display:'block', margin: '1% auto', width: '80%'}}>{movies.Title}</h4> </Link>
              <img className = 'poster' src={movies.Poster} alt="poster"/>
              <button className='fav' onClick={() => props.addToFavorites({title: movies.Title, id: movies.imdbID})}>‚ù§</button>
            </div>
            )
         }
        </ul>
      </div>
    );

}

function mapStateToProps(state) {
  return {
    moviesListed: state.moviesListed
  }
}

function mapDispatchToProps(dispatch) {
  return {
    getMovies: title => dispatch(getMovies(title)),
    addToFavorites: (id) => dispatch(addToFavorite(id)),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Buscador);
